import { useState } from "react"

const TodoLis = () => {
    const [todoList, setTodoList] = useState([])
    const [newTodo, setNewTodo] = useState('')

    const addTodu = () => {
        if (newTodo === "") return;
        setTodoList([...todoList, newTodo])
    }

    const removTodo = (todo) => {
        setTodoList(todoList.filter((item) => item !== todo))
    }

    console.log(todoList);

    return (
        <div>
            <input type="text" value={newTodo} onChange={(e) => setNewTodo(e.target.value.trim())} />
            <button onClick={addTodu} >click</button>
            <ul>
                {todoList.map((item) => (
                    <li>{item}<button onClick={() => removTodo(item)}>X</button></li>

                ))}
            </ul>
        </div>
    )

}

export default TodoLis;
