import { useState } from "react"

const TodoLis = () => {
    const [todoList, setTodoList] = useState([])
    const [newTodo, setNewTodo] = useState('')

    const addTodu = () => {
        if (newTodo === "") return;
        setTodoList([...todoList, {title: newTodo, id: Date.naw }])
    }

    const removTodo = (idTodo) => {
        setTodoList(todoList.filter(({id}) => id !== idTodo))
    }

    console.log(todoList);

    return (
        <div>
            <input type="text" value={newTodo} onChange={(e) => setNewTodo(e.target.value.trim())} />
            <button onClick={addTodu} >click</button>
            <ul>
                {todoList.map(({title, id}) => (
                    <li key={id}> {title}<button onClick={() => removTodo(id)}>X</button></li>
                ))}
            </ul>
        </div>
    )

}

export default TodoLis;
